<template>
  <div class="computer_box">
      <h1>컴퓨터</h1>
      <img class="computer" src="../assets/image/question.png" alt="컴퓨터">
      <div class="computer_btn">
        <button @click="computerBtn" v-if="btnBoolean">결과보기</button>
        <button @click="resetBtn" v-if="!btnBoolean">다음으로</button>
      </div>
      <div class="winner_box" v-if="gameOver">
        {{ $store.state.playUser[0] }} 님이 {{ $store.state.user.length - 1}}명을 뚜드려패고 승리하였습니다.
      </div>
  </div>
</template>

<script>
export default {
  data() {
    return {
      computer: '',
      btnBoolean: true,
      gameOver: false,
    }
  },
  created () {
    const randomNum = Math.floor(Math.random() * 3);

    this.computer = this.$store.state.question[randomNum];
    this.$store.state.computer = this.computer;
  },
  methods: {
    computerBtn() {
      
      for(let i = 0; this.$store.state.userQuestion.length > i; i++){
        if(this.$store.state.userQuestion[i] == 'paper' || this.$store.state.userQuestion[i] == 'rock' || this.$store.state.userQuestion[i] == 'scissors') {

        } else {
          alert('선택 안한 유저가 있습니다.');
          return true;
        }
      }
      if(this.$store.state.userQuestion.length !== this.$store.state.playUser.length) {
        alert('선택 안한 유저가 있습니다.');
        return true;
      }

      const computerData = this.computer;
      const computerImg = document.querySelector('.computer');

      if(computerData == 'paper') {
        computerImg.setAttribute('src', '/img/paper.783650f6.png');
      } else if (computerData == 'rock') {
        computerImg.setAttribute('src', '/img/rock.04c21188.png');
      } else {
        computerImg.setAttribute('src', '/img/scissors.d55df7f9.png');
      }
      this.$store.dispatch('resultBtn');
      this.btnBoolean = false;
      if(this.$store.state.playUser.length <= 1) {
        this.gameOver = true;
      }

    },
    resetBtn() {
      if(this.gameOver == true) {
        alert('게임이 끝났습니다. 재시작을 원하시는경우 새로고침해주세요.');
        return true;
      }

      this.$store.state.stageCount++;
      this.$store.state.userQuestion = [];
      this.$store.state.userBoolean = [];
      this.btnBoolean = true;

      document.querySelector('.computer').setAttribute('src', 'data:image/png;base64,');
      
      const userImg = document.querySelectorAll('.img_opt img');
      for(let i = 0; userImg.length > i; i++) {
        userImg[i].setAttribute('src', 'data:image/png;base64,');
      }
      const randomNum = Math.floor(Math.random() * 3);

      this.computer = this.$store.state.question[randomNum];
      this.$store.state.computer = this.computer;
      this.$router.push(`stage${this.$store.state.stageCount}`);
    }
  }
}
</script>

<style scoped>
.computer {
  margin: 50px 0;
}
.computer_btn {
  margin-bottom: 50px;
}
.computer_btn button {
  width: 80%;
  padding: 15px;
  border: 0;
  font-weight: bold;
  box-shadow: 0 5px 15px #b1a500;
  cursor: pointer;
  background-color: #ffee00;
}
</style>